import React from 'react';
import TaskItem from './TaskItem.js';

function GetTask(){
    var month = "";
    const date = new Date();
    var day = date.getDate();
    var monthNum = date.getMonth();

    switch(monthNum){
        case 1: {
            month = "January";
            break;
        }
        case 2:
            month = "Febuary";
            break;
        case 3:
            month = "March";
            break;
        case 4:
            month = "April";
            break;
        case 5:
            month = "May";
            break;
        case 6:
            month = "June";
            break;
        case 7:
            month = "July";
            break;
        case 8:
            month = "August";
            break;
        case 9:
            month = "September";
            break;
        case 10:
            month = "October";
            break;
        case 11:
            month = "November";
            break;
        case 12:
            month = "December";
            break; 
        default:
            month = "N/A";
            break;
    }
    var format = day + " " + month;

    function handleGetTask(t){
        var title = '';
        var desc = '';
        if(t.date === day){
            title = t.title;
            desc = t.description;
        }
        return(
            <div>
                <h4>{title}</h4>
                <p>{desc}</p>
            </div>
        );
    }

    return (
        <div>
            <p>{format}</p>
            {TaskItem.map(handleGetTask)}
        </div>
    );
}

export default GetTask;
