import React, { useState } from 'react';
import CreateArr from './CreateArr.jsx';

function Tasks(){
    const [arr, setArr] = useState(CreateArr);
    const [taskNo, setTaskNo] = useState();
    var x = 0;
    console.log(taskNo);

    function updateArr(){
        arr.splice(taskNo, 0, <li>{"NEW TASK"}</li>);
        setArr([...arr]);
    };

    function handleChange(e){
        x  = e.target.value;
        if(x!==null){
            setTaskNo(x);
        }
    }

    return (
        <div>
        <div className = "tasks">
            {arr}
        </div>
        <input onChange = {handleChange} placeholder = "Enter Event Date"/>
        <button className = "btn" onClick = {updateArr}>update arr</button>
        </div>
    );
}

export default Tasks;
