import React, { useState } from 'react';
import Events from './Events.js';

function Tasks(){

    const dates = [1,2,3,4,5,6,7];
    const months = ["jan", "feb", "march"];
    var [arr, update] = useState([]);
    var [date, updateDate] = useState("");
    var [event, updateEvent] = useState("");

    function getArr(){
        for(let i = 0; i <months.length; i++){
            for(let j = 0; j<dates.length; j++){
                arr.push(dates[j] + " " + months[i]);    
                arr.push("empty");
            }
        }  

      /*  if(arr.includes(date)){
            var x = arr.indexOf(date);
            console.log("found " +date+"!");
            console.log(x);
            
        } else{
            console.log("Could not find"+ date+"!");
        }*/
    }

    function handleClick(){
        console.log("li clicked!");
    }

    function inputDate(d){
        console.log(d.target.value);
        if(d.target.value!=""){
            date = d.target.value;
        }
    }

    function inputEvent(e){
        console.log(e.target.value);
        if(e.target.value.length!=""){
            event = e.target.value;
        }
    }

    function showDesc(s){
        return s.date + s.description;
    }

    function buttonClick(){
        console.log("button clicked!");
        Events.push({
            id: Events.length-1,
            date: date,
            description: event
        }); 
        console.log(Events.map(showDesc));
    }

    return(
        <div className = "tasksContainer">
            {getArr()}
            {arr.map((e) =>{return <li onClick = {handleClick}>{e}</li>})}
            <form>
                <input type="text" value = "" onChange = {inputDate} placeholder = "Enter date"/>
                <input type="text" value = "" onChange = {inputEvent} placeholder = "Enter event description"/>
            </form>
            <button onClick={buttonClick}>Add event</button>
        </div>
    );

}

export default Tasks;

