import React, {useState} from 'react';

function Tasks(){

    const [Arr, updateArr] = useState([1,2,3]);

    const addEntryClick = () => {
        updateArr([Arr.splice(2, 0, `Entry ${Arr.length}`)]);
    };
    
    return [
        <input type="button" onClick={addEntryClick} value="Add" />,
        <div>{Arr.map(entry => <div>{entry}</div>)}
        </div>
    ];

}

export default Tasks;
