function Tasks(){

    const [arr, setArr] = useState("1");

    console.log(arr);

    function updateArr(){
        setArr(arr + " 2");
        console.log(arr);
    };

    return (
        <div>
            {arr}
            <button className = "btn" onClick = {updateArr}>update arr</button>
        </div>
    );
}

export default Tasks;
