import React, { useState } from 'react';

function Tasks(){

    const dates = [1,2,3,4,5,6,7];
    const months = ["jan", "feb", "march"];
    const arr = [];
    const arr1 = [];

    function getArr(){
        for(let i = 0; i <months.length; i++){
            for(let j = 0; j<dates.length; j++){
                arr.push(dates[j] + " " + months[i]);
            }
        }
    }

    function doSplice(){
        arr.splice(2, 0, "NewTask");
    }

    function disp2(da){
        return da;
    }

    return(
        <div className = "tasks">
            {getArr()}
            {arr.map(disp2)}
            <button onClick = {doSplice}>Add task</button>
        </div>
    );

}

export default Tasks;
