import React, { useState } from 'react';

function Tasks(){

    const [arr, setArr] = useState([1,2,3,4,5]);

    console.log(arr);

    function updateArr(){
        arr.splice(2, 0, 45);
        setArr([...arr]);
        console.log(arr);
    };

    return (
        <div>
            {arr}
            <button className = "btn" onClick = {updateArr}>update arr</button>
        </div>
    );
}

export default Tasks;
